"use strict";
/*
 * 基本数据类
 * 1、数值
 * 2、字符值
 * 3、布尔值
 * 4、null && undefined
 * 5、void 函数无返回值
 */
// 数值
var num0 = 10; //整数
var num1 = 10.5; //浮点数
var num2 = 2; //进制数
// js中常见进制格式 二进制0b开头 八进制0开头 16进制0x开头
var num3 = NaN; // not a number
var num4 = Infinity; // 无穷数
// 字符
var str0 = 'string'; //普通字符
var str1 = "string"; //ES6 模板字符
// 
